 drop table if exists Possede; drop table if exists EstFait; drop table if exists Trie; drop table if exists Categorie; drop table if exists Municipalite; drop table if exists Matiere; drop table if exists Region; drop table if exists Poubelle; drop table if exists Produit;   create table Produit ( EAN decimal (30), nomP varchar (50), constraint pk_produit primary key(EAN) );  create table Region ( numeroR decimal (10), nomR varchar (50), constraint pk_region primary key(numeroR) );  create table Matiere ( identifiantMat decimal (10), nomMat varchar (50), constraint pk_matiere primary key(identifiantMat) );  create table EstFait ( EAN decimal(30), identifiantMat decimal(10), constraint fk_estfait_ean foreign key (EAN) references Produit(EAN), constraint fk_estfait_identifiantMat foreign key (identifiantMat) references Matiere(identifiantMat), constraint pk_estfait primary key (EAN,identifiantMat) );  create table Municipalite ( idMun decimal (10), nomMun varchar (50), ville varchar (50), codePostal varchar (10), idRegion decimal (10), constraint pk_municipalite primary key(idMun), constraint fk_municipalite_idRegion foreign key(idRegion) references Region(numeroR) );  create table Poubelle ( idPoub decimal (10), couleurPoub varchar (50), constraint pk_poubelle primary key(idPoub) );   create table Trie ( idPoub decimal(10), idMun decimal(10), identifiantMat decimal (30), consigneTri varchar(50), constraint fk_trie_idPoub foreign key (idPoub) references Poubelle(idPoub), constraint fk_trie_idMun foreign key (idMun) references Municipalite(idMun), constraint fk_trie_identifiantMat foreign key (identifiantMat) references Matiere(identifiantMat), constraint pk_trie primary key (idPoub, idMun, identifiantMat) );   create table Categorie ( idC decimal (10), nomC varchar (50),  constraint pk_categorie primary key(idC), constraint fk_sous_categorie foreign key (idC) references Categorie (idC) );   create table Possede( idC decimal(10), EAN decimal(30),  constraint fk_possede_idC foreign key(idC) references Categorie(idC), constraint fk_possede_ean foreign key(EAN) references Produit(EAN), constraint pk_possede primary key (idC,EAN) );   